import React, { Component } from "react";
import AppBar from "@material-ui/core/AppBar";
import TopBar from "./TopBar.js";
import { ThemeProvider as MuiThemeProvider } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import { withStyles } from "@material-ui/core/styles";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormLabel from "@material-ui/core/FormLabel";
import HelpIcon from "@material-ui/icons/Help";
import Tooltip from "@material-ui/core/Tooltip";

const useStyles = (theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 350,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  formControlSmall: {
    margin: theme.spacing(1),
    minWidth: 350,
    color: "gray",
  },
});

const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText("#859438"),
    backgroundColor: "#859438",
    "&:hover": {
      backgroundColor: "#859438",
    },
    margin: theme.spacing(1),
  },
}))(Button);

class FormPartnerDetails extends Component {
  continue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  };

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  };

  render() {
    const { values, handleChange, classes } = this.props;
    return (
      <MuiThemeProvider>
        <>
          <TopBar />
          <br />
          <h1 className={classes.formControl}>Partner Preferences</h1>
          <h4 className={classes.formControlSmall}>
            Disclaimer: We do our best to accommodate partner
            preferences—however, we can’t guarantee that there is someone in the
            program who is the perfect match.
          </h4>
          <br />
          <h2 className={classes.formControl}>
            Would you like a partner who studies a particular major? If so,
            please include below:
          </h2>
          <h4 className={classes.formControlSmall}>
            If no preference, please leave blank. If adding multiple majors,
            please separate using commas.
          </h4>
          <br />
          <TextField
            placeholder="Enter Your Preferred Major(s)"
            label="Preferred Major(s)"
            onChange={handleChange("major")}
            defaultValue={values.major}
            margin="normal"
            required
            className={classes.formControl}
          />
          <FormControl
            component="fieldset"
            required
            className={classes.formControl}
          >
            <FormLabel component="legend">
              On a scale from 1-5, how important is it to get someone in your
              pair/trio with the same major?
            </FormLabel>
            <RadioGroup
              row
              aria-label="majorWeight"
              name="majorWeight1"
              defaultValue={values.majorWeight}
              onChange={handleChange("majorWeight")}
            >
              <FormControlLabel value="1" control={<Radio />} label="1" />
              <FormControlLabel value="2" control={<Radio />} label="2" />
              <FormControlLabel value="3" control={<Radio />} label="3" />
              <FormControlLabel value="4" control={<Radio />} label="4" />
              <FormControlLabel value="5" control={<Radio />} label="5" />
            </RadioGroup>
          </FormControl>
          <br />
          <h2 className={classes.formControl}>
            Would you like a partner who studies a particular gender? If so,
            please include below:
          </h2>
          <h4 className={classes.formControlSmall}>
            If no preference, please leave blank.
          </h4>
          <br />
          <FormControl className={classes.formControl}>
            <InputLabel id="gender-label">Preferred Gender</InputLabel>
            <Select
              labelId="gender-label"
              id="gender"
              defaultValue={values.gender}
              onChange={handleChange("gender")}
            >
              <MenuItem value={"Male"}>Male</MenuItem>
              <MenuItem value={"Female"}>Female</MenuItem>
              <MenuItem value={"Non-Binary"}>Non-Binary</MenuItem>
            </Select>
          </FormControl>
          <FormControl
            component="fieldset"
            required
            className={classes.formControl}
          >
            <FormLabel component="legend">
              On a scale from 1-5, how important is it to get someone in your
              pair/trio with the same gender?
            </FormLabel>
            <RadioGroup
              row
              aria-label="genderWeight"
              name="genderWeight1"
              defaultValue={values.genderWeight}
              onChange={handleChange("genderWeight")}
            >
              <FormControlLabel value="1" control={<Radio />} label="1" />
              <FormControlLabel value="2" control={<Radio />} label="2" />
              <FormControlLabel value="3" control={<Radio />} label="3" />
              <FormControlLabel value="4" control={<Radio />} label="4" />
              <FormControlLabel value="5" control={<Radio />} label="5" />
            </RadioGroup>
          </FormControl>
          <br />
          <ColorButton
            variant="contained"
            color="primary"
            className={classes.margin}
            onClick={this.back}
          >
            Back
          </ColorButton>
          <ColorButton
            variant="contained"
            color="primary"
            className={classes.margin}
            onClick={this.continue}
          >
            Next
          </ColorButton>
        </>
      </MuiThemeProvider>
    );
  }
}

export default withStyles(useStyles)(FormPartnerDetails);
