import React, { Component } from "react";
import TopBar from "./TopBar.js";
import { ThemeProvider as MuiThemeProvider } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import { withStyles } from "@material-ui/core/styles";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormLabel from "@material-ui/core/FormLabel";

const useStyles = (theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 150,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
});

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};
const names = [
  "Oliver Hansen",
  "Van Henry",
  "April Tucker",
  "Ralph Hubbard",
  "Omar Alexander",
  "Carlos Abbott",
  "Miriam Wagner",
  "Bradley Wilkerson",
  "Virginia Andrews",
  "Kelly Snyder",
];
function getStyles(name, personName, theme) {
  return {
    fontWeight:
      personName.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

class FormUserDetails extends Component {
  continue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  };
  render() {
    const { values, handleChange, classes, theme } = this.props;
    return (
      <MuiThemeProvider>
        <>
          <TopBar />
          <h2>Basic Information</h2>
          <TextField
            placeholder="Enter Your First Name"
            label="First Name"
            onChange={handleChange("firstName")}
            defaultValue={values.firstName}
            margin="normal"
          />
          <TextField
            placeholder="Enter Your Last Name"
            label="Last Name"
            onChange={handleChange("lastName")}
            defaultValue={values.lastName}
            margin="normal"
          />
          <br />
          <TextField
            placeholder="Enter Your Email"
            label="Email"
            onChange={handleChange("email")}
            defaultValue={values.email}
            margin="normal"
            fullWidth
          />
          <br />
          <TextField
            placeholder="Enter Your Student ID Number"
            label="SID"
            onChange={handleChange("sid")}
            defaultValue={values.sid}
            margin="normal"
            fullWidth
          />
          <br />
          <FormControl className={classes.formControl}>
            <InputLabel id="academic-title-label">Academic Title</InputLabel>
            <Select
              labelId="academic-title-label"
              id="academic-title"
              defaultValue={values.academicTitle}
              onChange={handleChange("academicTitle")}
            >
              <MenuItem value={"Undergraduate"}>Undergraduate</MenuItem>
              <MenuItem value={"Graduate"}>Graduate</MenuItem>
            </Select>
          </FormControl>
          <FormControl className={classes.formControl}>
            <InputLabel id="residency-label">Residency</InputLabel>
            <Select
              labelId="residency-label"
              id="residency"
              defaultValue={values.residency}
              onChange={handleChange("residency")}
            >
              <MenuItem value={"Domestic US"}>Domestic US</MenuItem>
              <MenuItem value={"International"}>International</MenuItem>
            </Select>
          </FormControl>
          <br />
          <TextField
            placeholder="Enter Your Major"
            label="Major"
            onChange={handleChange("major")}
            defaultValue={values.major}
            margin="normal"
          />
          <FormControl component="fieldset">
            <FormLabel component="legend">
              On a scale from 1-5, how important is it to get someone in your
              pair/trio with the same major?
            </FormLabel>
            <RadioGroup
              row
              aria-label="majorWeight"
              name="majorWeight1"
              defaultValue={values.majorWeight}
              onChange={handleChange("majorWeight")}
            >
              <FormControlLabel value="1" control={<Radio />} label="1" />
              <FormControlLabel value="2" control={<Radio />} label="2" />
              <FormControlLabel value="3" control={<Radio />} label="3" />
              <FormControlLabel value="4" control={<Radio />} label="4" />
              <FormControlLabel value="5" control={<Radio />} label="5" />
            </RadioGroup>
          </FormControl>
          <br />
          <FormControl className={classes.formControl}>
            <InputLabel id="gender-label">Gender</InputLabel>
            <Select
              labelId="gender-label"
              id="gender"
              defaultValue={values.gender}
              onChange={handleChange("gender")}
            >
              <MenuItem value={"Male"}>Male</MenuItem>
              <MenuItem value={"Female"}>Female</MenuItem>
              <MenuItem value={"Non-Binary"}>Non-Binary</MenuItem>
            </Select>
          </FormControl>
          <FormControl component="fieldset">
            <FormLabel component="legend">
              On a scale from 1-5, how important is it to get someone in your
              pair/trio with the same gender?
            </FormLabel>
            <RadioGroup
              row
              aria-label="genderWeight"
              name="genderWeight1"
              defaultValue={values.genderWeight}
              onChange={handleChange("genderWeight")}
            >
              <FormControlLabel value="1" control={<Radio />} label="1" />
              <FormControlLabel value="2" control={<Radio />} label="2" />
              <FormControlLabel value="3" control={<Radio />} label="3" />
              <FormControlLabel value="4" control={<Radio />} label="4" />
              <FormControlLabel value="5" control={<Radio />} label="5" />
            </RadioGroup>
          </FormControl>
          <br />
          <FormControl className={classes.formControl}>
            <InputLabel id="demo-mutiple-name-label">Name</InputLabel>
            <Select
              labelId="demo-mutiple-name-label"
              id="demo-mutiple-name"
              multiple
              value={personName}
              onChange={handleChange}
              input={<Input />}
              MenuProps={MenuProps}
            >
              {names.map((name) => (
                <MenuItem
                  key={name}
                  value={name}
                  style={withStyles(name, personName, theme)}
                >
                  {name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <br />
          <Button variant="contained" onClick={this.continue}>
            Continue
          </Button>
        </>
      </MuiThemeProvider>
    );
  }
}

export default withStyles(useStyles)(FormUserDetails);
