import React, { Component } from "react";
import Timesheet from "./Timesheet";
import TimesheetSuccess from "./TimesheetSuccess";

class TimesheetController extends Component {
  constructor(props) {
    super(props);

    this.state = {
      step: 1,
      firstName: "",
      lastName: "",
      sid: "",
      partnerNames: "",
      week: "",
      hours: "",
      allWeeks: [],
      calendarLink: "",
      semester: "",
    };
  }

  componentDidMount() {
    const { REACT_APP_TSRENDER } = process.env;
    fetch(REACT_APP_TSRENDER)
      .then((response) => response.json())
      .then((data) => {
        this.setState({ semester: data.semester });
      })
      .catch((error) => console.log("Error", error));
  }

  // Proceed to next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1,
    });
  };

  // Handle fields change
  handleChange = (input) => (e) => {
    this.setState({ [input]: e.target.value });
  };

  render() {
    const { step } = this.state;
    const {
      firstName,
      lastName,
      sid,
      partnerNames,
      weeks,
      hours,
      allWeeks,
      calendarLink,
      semester,
    } = this.state;
    const values = {
      firstName,
      lastName,
      sid,
      partnerNames,
      weeks,
      hours,
      allWeeks,
      calendarLink,
      semester,
    };
    switch (step) {
      case 1:
        {
          console.log(this.state.semester);
        }
        return (
          <Timesheet
            handleChange={this.handleChange}
            nextStep={this.nextStep}
            values={values}
          />
        );
      case 2:
        return <TimesheetSuccess />;
      default:
        console.log("This is a multi-step form built with React.");
    }
  }
}

export default TimesheetController;
