import React, { Component } from "react";
import TopBar from "./TopBar.js";
import AppBar from "@material-ui/core/AppBar";
import { ThemeProvider as MuiThemeProvider } from "@material-ui/core/styles";
import { List, ListItem, ListItemText } from "@material-ui/core/";
import Button from "@material-ui/core/Button";

export class Confirm extends Component {
  continue = (e) => {
    e.preventDefault();
    // PROCESS FORM //
    this.props.nextStep();
  };

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  };

  render() {
    const {
      values: {
        firstName,
        lastName,
        email,
        sid,
        academicTitle,
        residency,
        major,
        majorWeight,
        gender,
        genderWeight,
        hobbies,
        availability,
        hopeToGain,
        planToMeet,
        occupation,
        city,
        bio,
      },
    } = this.props;
    return (
      <MuiThemeProvider>
        <>
          <TopBar />
          <AppBar title="Confirm User Data" />
          <List>
            <ListItem>
              <ListItemText primary="First Name" secondary={firstName} />
            </ListItem>
            <ListItem>
              <ListItemText primary="Last Name" secondary={lastName} />
            </ListItem>
            <ListItem>
              <ListItemText primary="Email" secondary={email} />
            </ListItem>
            <ListItem>
              <ListItemText primary="SID" secondary={sid} />
            </ListItem>
            <ListItem>
              <ListItemText
                primary="Academic Title"
                secondary={academicTitle}
              />
            </ListItem>
            <ListItem>
              <ListItemText primary="Residency" secondary={residency} />
            </ListItem>
            <ListItem>
              <ListItemText primary="Major" secondary={major} />
            </ListItem>
            <ListItem>
              <ListItemText
                primary="Major Preference Level"
                secondary={majorWeight}
              />
            </ListItem>
            <ListItem>
              <ListItemText primary="Gender" secondary={gender} />
            </ListItem>
            <ListItem>
              <ListItemText
                primary="Gender Preference Level"
                secondary={genderWeight}
              />
            </ListItem>
            <ListItem>
              <ListItemText primary="Hobbies" secondary={hobbies.join(", ")} />
            </ListItem>
            <ListItem>
              <ListItemText
                primary="Days of Week Availability"
                secondary={availability.join(", ")}
              />
            </ListItem>
            <ListItem>
              <ListItemText
                primary="What do you hope to gain from the SLC Language Exchange Program?"
                secondary={hopeToGain}
              />
            </ListItem>
            <ListItem>
              <ListItemText
                primary="How do you plan to maintain your motivation to meet with your partner(s) weekly?"
                secondary={planToMeet}
              />
            </ListItem>
          </List>
          <br />

          <Button color="secondary" variant="contained" onClick={this.back}>
            Back
          </Button>

          <Button color="primary" variant="contained" onClick={this.continue}>
            Confirm & Continue
          </Button>
        </>
      </MuiThemeProvider>
    );
  }
}

export default Confirm;
